import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'
import { Button } from 'reactstrap';
import { useParams } from "react-router-dom"
import Switch from "react-switch";


const url = window.location.href
  if(url.includes("http://localhost:3000/user/")){
    require("./style.css")
    require("bootstrap/dist/css/bootstrap.min.css")
}

function Profile() {
    const { id: userid } = useParams()
    const [username, setUsername] = useState("")
    const [email , setEmail] = useState("")
    const [website , setWebsite] = useState("")
    const [instagram , setInstagram] = useState("")
    const [twitter , setTwitter] = useState("")
    const [github , setGithub] = useState("")
    const [facebook, setFacebook] = useState("")
    const [firstname, setFirstName] = useState("")
    const [lastname, setLastName] = useState("")
    const [bio, setBio] = useState("")
    const [bio2, setBio2] = useState("")

    const [checked, setChecked] = useState(false);
  const handleChange = nextChecked => {
    setChecked(nextChecked);
  };

    useEffect(() => {
      Axios.post("http://localhost:3001/api/profilepage", {
        id : userid,
        }).then((response)  => {
          if(response.data.error){
            console.log(response.data.error)
          }else{
            if(response.data.username){
              setUsername(response.data.username)
            }else{
              setUsername("UNKNOWN")
            }
            if(response.data.email){
              setEmail(response.data.email)
            }else{
              setEmail("UNKNOWN")
            }
            if(response.data.website){
              setWebsite(response.data.website)
            }else{
              setWebsite("UNKNOWN")
            }
            if(response.data.github){
              setGithub(response.data.github)
            }else{
              setGithub("UNKNOWN")
            }
            if(response.data.twitter){
              setTwitter(response.data.twitter)
            }else{
              setTwitter("UNKNOWN")
            }
            if(response.data.instagram){
              setInstagram(response.data.instagram)
            }else{
              setInstagram("UNKNOWN")
            }
            if(response.data.facebook){
              setFacebook(response.data.facebook)
            }else{
              setFacebook("UNKNOWN")
            }
            if(response.data.firstname){
              setFirstName(response.data.firstname)
            }else{
              setFirstName("UNKNOWN")
            }
            if(response.data.lastname){
              setLastName(response.data.lastname)
            }else{
              setLastName("UNKNOWN")
            }
            if(response.data.firstbio){
              setBio(response.data.firstbio)
            }else{
              setBio("UNKNOWN")
            }
            if(response.data.secbio){
              setBio2(response.data.secbio)
            }else{
              setBio2("UNKNOWN")
            }
          }
          
      });
    }, [])
    
    return (
      
        <div className="container">
           <div className="example">
            <h2>Simple usage</h2>
            <label>
              <span>Switch with default style</span>
              <Switch
                onChange={handleChange}
                checked={checked}
                className="react-switch"
              />
            </label>
            <p>
              The switch is <span>{checked ? "on" : "off"}</span>.
            </p>
          </div>
        
      </div>
      
    );
  }
  
  export default Profile;
