import "./design.css"
import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'




  

function Login() {

    const [usernameoremail, setUsername] = useState("")
    
    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");


    const resetpassword = (e) =>{
      e.preventDefault();
      setErrorMsg("");
      setSuccesMsg("");

      Axios.post("http://localhost:3001/api/passwordreset", {
          username: usernameoremail,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
          }
      });
    };
    return (
      <>
          <div class="form-body">
            <div class="row">
                <div class="form-holder">
                    <div class="form-content">
                        <div class="form-items">
                          <br/>
                            <h3>Login</h3>
                            {(() => {
                              if (errormsg != "") {
                                  return (
                                      <div class="invalid-feedback mv-up">
                                        {errormsg}
                                      </div>
                                  )
                              } 
                            })()}
                            {(() => {
                              if (succesMsg != "") {
                                  return (
                                      <div class="valid-feedback mv-up">
                                        {succesMsg}
                                      </div>
                                  )
                              } 
                            })()}  
                            <form class="requires-validation" novalidate>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="username" 
                                  placeholder="Username"
                                  onChange={(e) => {
                                    setUsername(e.target.value)
                                  }}   
                                  required/>
                                </div>
                            <br/>
                            <label class="form-check-label"> Not signed up yet? <a href="./register" style={{ color: '#FFF' }} >Register</a></label>
                            <br/>
                            <br/>
                                <div class="form-button mt-3">
                                    <button id="submit" type="submit" class="btn btn-primary" onClick={resetpassword} >Reset Password</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>

      </>
    );
  }
  
  export default Login;
