import "./design.css"
import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'




  

function Login() {
    let history = useHistory();

    Axios.defaults.withCredentials = true;

    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    //Login checker
    const [loggedin , setLoggedIn] = useState(false);

  
    Axios.defaults.withCredentials = true;

    Axios.get("http://localhost:3001/api/check", {
        }).then((response)  => {
            if(!response.data.error){
                console.log(response.data.error)
                history.push('/dashboard')
            }
    });

    const login = (e) =>{
      e.preventDefault();
      setErrorMsg("");
      setSuccesMsg("");
      Axios.post("http://localhost:3001/api/login", {
          username: username,
          password: password,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
          }
      });
    };
    return (
      <>
          <div class="row">
                <div class="form-holder">
                    <div class="form-content">
                        <div class="form-items">
                          <br/>
                            <h3>Login</h3>
                            {(() => {
                              if (errormsg != "") {
                                  return (
                                      <div class="invalid-feedback mv-up">
                                        {errormsg}
                                      </div>
                                  )
                              } 
                            })()}
                            {(() => {
                              if (succesMsg != "") {
                                  return (
                                      <div class="valid-feedback mv-up">
                                        {succesMsg}
                                      </div>
                                  )
                              } 
                            })()}  
                            <form class="requires-validation" novalidate>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="username" 
                                  placeholder="username"
                                  onChange={(e) => {
                                    setUsername(e.target.value)
                                  }}   
                                  required/>
                                </div>
                              <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="password" 
                                  name="password" 
                                  placeholder="Password"
                                  onChange={(e) => {
                                    setPassword(e.target.value)
                                 }}
                                  required/>
                              </div>
                            <br/>
                            <label class="form-check-label"> Not signed up yet? <a href="./register" style={{ color: '#FFF' }} >Register</a></label>
                            <br/>
                            <br/>
                                <div class="form-button mt-3">
                                    <button id="submit" type="submit" class="btn btn-primary" onClick={login} >Register</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

      </>
    );
  }
  
  export default Login;
