import React, {useCallback, useEffect, useState} from 'react';
import {AnimationType, IToast, ToastOptions} from "./index";
import {closeIcon, iconIllustrationTypes} from "./SvgIcons";

interface IToasts {
    toasts?: Array<IToast>;
    hideToast: (toastId: string) => void;
}

export enum ToastPosition {
    TOP_RIGHT = 'top-right',
    BOTTOM_RIGHT = 'bottom-right',
    BOTTOM_CENTER = 'bottom-center'
}

let DefaultToastPosition = ToastPosition.BOTTOM_RIGHT;

export const setToastPosition = (position: ToastPosition) => {
    DefaultToastPosition = position;
}

export default function Toast({toasts, hideToast}: IToasts) {

    return (
        <div className={`react-custom-toast-container react-custom-toast-${DefaultToastPosition}`}>
            {toasts && toasts.map(toast => <ToastItem key={toast.id} toast={toast} hideToast={hideToast}/>)}
        </div>
    )

}

interface IProps {
    toast: IToast,
    animationType?: AnimationType;
    hideToast: (toastId: string) => void;
}

function ToastItem({toast, hideToast}: IProps) {

    const [toastData, setToast] = useState<ToastOptions | null>(null);

    const [animation, setAnimation] = useState<{ in: string, out: string }>({in: '', out: ''});

    let timeout: NodeJS.Timeout;

    useEffect(() => {
        if (toastData && !toast) {
            hideMe();
        } else if (toast && !toastData) {
            showMe();
            if (toast.timeoutDuration) {
                timeout = setTimeout(hideMe, toast.timeoutDuration)
            }

        }
        return () => clearTimeout(timeout);
    }, [toast])

    useEffect(() => {

        if (animation.in && toast) {
            setToast(toast);
        }

        if (animation.out) {
            timeout = setTimeout(() => {
                setToast(null);
                hideToast(toast.id);
            }, 500);
        }

    }, [animation])

    const hideMe = useCallback(() => {
        if (DefaultToastPosition === ToastPosition.BOTTOM_CENTER) {
            setAnimation({out: 'animate__animated animate__slideOutDown animate__faster', in: ''})
        } else {
            setAnimation({out: 'animate__animated animate__slideOutRight animate__faster', in: ''})
        }
    }, []);

    const showMe = useCallback(() => {
        if (DefaultToastPosition === ToastPosition.BOTTOM_CENTER) {
            setAnimation({in: 'animate__animated animate__slideInUp animate__faster', out: ''})
        } else {
            setAnimation({in: 'animate__animated animate__slideInRight animate__faster', out: ''})
        }
    }, []);

    if (!toastData)
        return null;

    return (
        <div className={`react-custom-toast-${toastData.type} react-custom-toast ${animation.in} ${animation.out}`}
             style={{...toast.containerStyle}}>
            <div style={{
                width: '90%',
                display: 'flex',
                flexDirection: 'row',
                alignItems: 'center'
            }}>
                {toast.customComponent || (
                    <>
                        <div>
                            {iconIllustrationTypes[toastData.type]}
                        </div>
                        <div className={'react-custom-toast-text'} style={{...toast.textStyle}}>
                            {toastData.text}
                        </div>
                    </>
                )}
            </div>
            <div style={{width: "10%", cursor: 'pointer'}}
                 onClick={hideMe}>{closeIcon()}</div>
        </div>
    )

}

import React, {createElement, useCallback, useEffect, useState} from 'react';

interface IProps {
    hideModal: () => void;
    Component?: () => JSX.Element;
    ComponentJSX?: JSX.Element;
    componentProps: React.ComponentProps<any>
}

export default function Modal({hideModal, Component, ComponentJSX, componentProps}: IProps) {

    const [ModalToRender, setModalToRender] = useState<JSX.Element | undefined>(undefined);

    const [animation, setAnimation] = useState<{ in: string, out: string }>({in: '', out: ''});

    const {animationType, outAnimationType, allowOutsideClick = true} = componentProps;

    let timeout: NodeJS.Timeout;

    useEffect(() => {
        if (ModalToRender && !(Component || ComponentJSX)) {
            hideMe();
        } else if (Component || ComponentJSX) {
            showMe();
        }
        return () => clearTimeout(timeout);
    }, [Component, ComponentJSX, componentProps])

    useEffect(() => {

        if (animation.in && (Component || ComponentJSX)) {
            // @ts-ignore
            let c: JSX.Element | undefined;
            if (Component) {
                c = createElement(Component, {
                    ...componentProps, hideModal: hideModal
                });
            }
            setModalToRender(ComponentJSX || c);
        }

        if (animation.out) {
            if (!outAnimationType) {
                setModalToRender(undefined);
            } else
                timeout = setTimeout(() => {
                    setModalToRender(undefined);
                }, 600);
        }

    }, [animation])

    const hideMe = useCallback(() => {
        setAnimation({out: `animate__animated animate__${outAnimationType} animate__faster`, in: ''})
    }, [outAnimationType]);

    const showMe = useCallback(() => {
        setAnimation({out: '', in: `animate__animated animate__${animationType} animate__faster`})
    }, [animationType]);

    if (!ModalToRender)
        return null;

    return (
        <ModalWrapper>
            <ModalBackdrop onClick={allowOutsideClick ? hideModal : ()=>{}}/>
            <div className={animation.in || animation.out}>
                {ModalToRender}
            </div>
        </ModalWrapper>
    )

}

const ModalBackdrop = ({onClick}: { onClick: () => void }) => {
    useEffect(() => {
        document.body.classList.add("react-custom-modal-open");

        return () => {
            document.body.classList.remove("react-custom-modal-open");
        };
    }, []);

    return (
        <div
            onClick={onClick}
            className={`react-custom-modal-backdrop`}
        >
        </div>
    );
};

const ModalWrapper = ({children}: { children: any }) => (
    <div className="react-custom-modal-wrapper">{children}</div>
);

