            <label className={'react-custom-input-error-item'} style={errorMessageStyle ?? undefined}>
				{error}
            </label>
			}
		</div>
	)

}

import React, {ChangeEvent, Dispatch, SetStateAction, useRef} from 'react';
import {ImageInputProps, InputProps} from "./index";
import {closeIcon} from "./SvgIcons";

interface IProps {
    setInputValues: Dispatch<SetStateAction<{ [key: string]: any }>>;
    inputValues: { [key: string]: any };
    item: InputProps;
    error?: string;
    errorMessageStyle?: React.CSSProperties;
}

export default function ImageInput({setInputValues, inputValues, item, error, errorMessageStyle}: IProps) {

    const inputRef = useRef<HTMLInputElement>(null);

    const readImage = (event: ChangeEvent<HTMLInputElement>) => {
        if (event.target.files) {
            let imagePromises: Array<Promise<void>> = [];
            let images: Array<any> = [];
            Array.from(event.target.files).forEach((file: File) => {
                imagePromises.push(
                    new Promise((resolve) => {
                        var reader = new FileReader();
                        reader.onload = function (e) {
                            images.push({src: e.target!.result, value: file});
                            resolve();
                        };
                        reader.readAsDataURL(file);
                    })
                );
            });
            Promise.all(imagePromises).then(() => {
                setInputValues({
                    ...inputValues,
                    // @ts-ignore
                    [item.name]: [...images],
                });
                inputRef.current!.value! = "";
                // event.target.reset();
            });
        }
    };

    return (
        <>
            <div style={{textAlign: "left"}}>
                <label style={{display: "block", marginBottom: 5}}>{item.label || ''}</label>
                <div
                    style={{cursor: 'pointer'}}
                    className={'react-custom-input-item select-image'}
                    onClick={() => {
                        if (inputRef.current) inputRef.current.click();
                    }}
                >
                    Select Image
                </div>
                <input
                    ref={(ref) => {
                        // @ts-ignore
                        inputRef.current = ref;
                    }}
                    value={inputRef.current ? inputRef.current.value : ""}
                    multiple={(item as ImageInputProps).multiple || false}
                    className={'react-custom-input-item'}
                    onChange={(val) => readImage(val)}
                    style={{display: "none"}}
                    id={"image"}
                    type={"file"}
                />
            </div>
            <div style={{textAlign: "left"}}>
                {inputValues &&
                inputValues[item.name] &&
                // @ts-ignore
                inputValues[item.name].map((image, index) => (
                    <div
                        className={'react-custom-input-img-container'}
                        key={'image' + index}
                        style={{
                            position: "relative",
                        }}
                    >
                        <img alt="" style={{maxHeight: 100}} src={image.src}/>
                        <div
                            onClick={() => {
                                setInputValues({
                                    ...inputValues,
                                    // @ts-ignore
                                    [item.name]:
                                        [...inputValues[item.name].slice(0, index),
                                            ...inputValues[item.name].slice(index + 1)]
                                })
                            }
                            }
                            style={{
                                borderRadius: 5,
                                position: "absolute",
                                top: 5,
                                right: 5,
                                zIndex: 500,
                                cursor: 'pointer'
                            }}
                        >
                            {closeIcon('white')}
                        </div>
                    </div>
                ))}
            </div>
            {error &&
            <label className={'react-custom-input-error-item'} style={errorMessageStyle ?? undefined}>
                {error}
            </label>
            }
        </>
    )

}

import {InputProps} from "./index";

export interface ValidationResult {
	success: boolean,
	message?: string
}

const getDateWithoutTime = function (date: Date) {
	return new Date(date.toDateString());
}

export class InputValidation {

	private static DEFAULT_ERROR_MESSAGE = 'Invalid Value';

	public static validateDate(input: InputProps, value: string | undefined): ValidationResult {

		const validation = input.validation!;

		if (validation.required) {
			if (!value) {
				return {
					success: false,
					message: validation.required.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.minDate) {
			if (value && new Date(value).getTime() < getDateWithoutTime(validation.minDate.value).getTime()) {
				return {
					success: false,
					message: validation.minDate.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.maxDate) {
			if (value && new Date(value).getTime() > getDateWithoutTime(validation.maxDate.value).getTime()) {
				return {
					success: false,
					message: validation.maxDate.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		return {
			success: true
		}

	}

	public static validateText(input: InputProps, value: string | undefined): ValidationResult {

		const validation = input.validation!;

		if (validation.required) {
			if (!value) {
				return {
					success: false,
					message: validation.required.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.minLength) {
			if (value !== undefined && value.length < validation.minLength.value) {
				return {
					success: false,
					message: validation.minLength.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.maxLength) {
			if (value !== undefined && value.length > validation.maxLength.value) {
				return {
					success: false,
					message: validation.maxLength.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		return {
			success: true,
		}

	}

	public static validateNumber(input: InputProps, value: number | undefined): ValidationResult {

		const validation = input.validation!;

		if (validation.required) {
			if (!value) {
				return {
					success: false,
					message: validation.required.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.min) {
			if (value === undefined || value < validation.min.value) {
				return {
					success: false,
					message: validation.min.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		if (validation.max) {
			if (value !== undefined && value > validation.max.value) {
				return {
					success: false,
					message: validation.max.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		return {
			success: true
		}

	}

	public static validateFile(input: InputProps, value: File | undefined): ValidationResult {

		const validation = input.validation!;

		if (validation.required) {
			if (!value) {
				return {
					success: false,
					message: validation.required.message ?? this.DEFAULT_ERROR_MESSAGE
				}
			}
		}

		return {
			success: true
		}

	}

}

import React, {useState} from "react";
import "react-datepicker/dist/react-datepicker.css";
import {DialogType, InputProps, OptionDialogButton} from "./index";
import Header from "./Header";
import Footer from "./Footer";
import Input from "./Input";
import ImageInput from "./ImageInput";
import {InputValidation, ValidationResult} from "./Validation";

// @ts-ignore
Date.prototype.toDateInputValue = function () {
	var local = new Date(this);
	local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
	return local.toJSON().slice(0, 10);
};

interface DialogProps {
	title: string,

	text: string,

	type: DialogType,

	hideModal: () => {},

	options: Array<OptionDialogButton>,

	onConfirm: (result?: { [key: string]: any }) => {},

	onCancel: (result?: { [key: string]: any }) => {},

	isAlert: boolean,

	isInput: boolean,

	confirmText: string,

	cancelText: string,

	showCloseButton: boolean,

	inputs: Array<InputProps>,

	onDismissed: (result?: { [key: string]: any }) => {},

	onOpened: (result?: { [key: string]: any }) => {},

	input: InputProps,

	headerTextStyle?: React.CSSProperties;

	errorMessageStyle?: React.CSSProperties;

	textStyle?: React.CSSProperties;

	containerStyle?: React.CSSProperties;

	headerStyle?: React.CSSProperties;

	footerStyle?: React.CSSProperties;

	bodyComponent?: JSX.Element;
}


const Dialog = (props: DialogProps) => {

	const {
		title,
		text,
		type,
		hideModal,
		onConfirm = null,
		onCancel = null,
		isAlert = true,
		showCloseButton = false,
		isInput = false,
		confirmText = "Ok",
		cancelText = "Cancel",
		inputs = null,
		onDismissed = null,
		input,
		headerTextStyle,
		textStyle,
		containerStyle,
		headerStyle,
		footerStyle,
		bodyComponent,
		errorMessageStyle,
		options
	} = props;

	const getDefaultValues = () => {

		let defaultVals = {};

		if (input && !inputs && input.default) {
			if (input.inputType === 'date') {
				// @ts-ignore
				defaultVals[input.name] = (input.default as Date).toDateInputValue();
			} else {
				defaultVals[input.name] = input.default;
			}
		}
		if (inputs && !input) {

			inputs.forEach(i => {
				if (i.default) {
					if (i.inputType === 'date') {
						// @ts-ignore
						defaultVals[i.name] = (i.default as Date).toDateInputValue();
					} else {
						defaultVals[i.name] = i.default;
					}
				}
			})
		}

		return defaultVals;
	}


	const [inputValues, setInputValues] = useState<{ [key: string]: any }>(() => getDefaultValues());
	const [errorValues, setErrorValues] = useState<{ [key: string]: string }>({});

	const validateInputValue = (input: InputProps): { [key: string]: string } | null => {

		let result: ValidationResult;

		switch (input.inputType) {
			case "text":
				result = InputValidation.validateText(input, inputValues[input.name]);
				break;
			case "number":
				result = InputValidation.validateNumber(input, inputValues[input.name]);
				break;
			case "date":
				result = InputValidation.validateDate(input, inputValues[input.name]);
				break;
			case "file":
				result = InputValidation.validateFile(input, inputValues[input.name]);
				break;
			case "image":
				result = InputValidation.validateFile(input, inputValues[input.name]);
				break;
			default:
				result = {
					success: true,
					message: ''
				}
		}

		if (!result.success) {
			return {[input.name]: result.message!};
		}

		return null;

	}

	const validateInputs = (): { [key: string]: string } => {

		setErrorValues({});

		const allInputsToValidate = [];

		if (input && input.validation) {
			allInputsToValidate.push(input);
		}

		if (inputs) {
			for (const i of inputs) {
				if (i.validation) {
					allInputsToValidate.push(i);
				}
			}
		}

		let errorsMapping = {};

		allInputsToValidate.forEach((input) => {
			const res = validateInputValue(input);
			if (res !== null) {
				errorsMapping = {
					...errorsMapping,
					...res
				}
			}
		});

		return errorsMapping;

	}

	const confirmHandle = () => {

		const errors = validateInputs();

		if (Object.keys(errors).length === 0) {
			hideModal();
			if (onConfirm) {
				onConfirm(inputValues);
			}
			;
			if (onDismissed && isInput) onDismissed(inputValues);
		} else {
			setErrorValues(errors);
		}

	}

	const cancelHandle = () => {
		hideModal();
		if (onCancel) onCancel();
		if (onDismissed && isInput) onDismissed(inputValues);
	}

	let optionsToRender: Array<OptionDialogButton> = [];

	if (!isAlert)
		if (options) {
			optionsToRender = options.map(option => {
				return {
					...option,
					onClick: () => {
						if (option.type === 'confirm') {
							confirmHandle();
						} else if (option.type === 'cancel') {
							cancelHandle();
						} else {
						}
					}
				}
			});
		} else
			optionsToRender = [
				{
					name: cancelText,
					onClick: cancelHandle,
				},
				{
					name: confirmText,
					onClick: confirmHandle,
				},
			];
	else
		optionsToRender = [
			{
				name: confirmText,
				onClick: () => {
					hideModal();
				},
			},
		];

	let inputsToRender: InputProps[] = [];

	if (isInput) {
		if (!inputs) inputsToRender = [input];
		else inputsToRender = inputs;
	}

	return (
		<div className={'react-custom-dialog-wrapper'} style={{...containerStyle}}>
			<Header headerStyle={headerStyle} headerTextStyle={headerTextStyle} showCloseButton={showCloseButton}
			        type={type}
			        hideModal={hideModal} title={title}/>
			{bodyComponent || (
				<>
					{text && text !== "" ?
						<div className={'react-custom-body-text'} style={{...textStyle}}>{text}</div> : null}
					{isInput && (
						<div className={'react-custom-inputs-container'}>
							<>
								{inputsToRender.map((item, index) =>
									<div key={`input${index}`} className={'react-custom-input-container'}>
										{item.inputType !== 'image' ? (
												<Input errorMessageStyle={errorMessageStyle} error={errorValues[item.name]} item={item} setInputValues={setInputValues}
												       inputValues={inputValues}/>) :
											<ImageInput errorMessageStyle={errorMessageStyle} error={errorValues[item.name]} item={item} setInputValues={setInputValues}
											            inputValues={inputValues}/>}
									</div>
								)}
							</>
						</div>
					)}
				</>
			)
			}
			<Footer footerStyle={footerStyle} optionsToRender={optionsToRender}/>
		</div>
	);
};

export default Dialog;

import React, {
    createContext,
    useContext,
    useReducer
} from "react";
import {ModalRoot} from "./component";
import Dialog from "./Dialog";
import {ToastPosition} from "./Toast";

export enum AnimationType {
    FADE_IN = 'fadeIn',
    FADE_IN_UP = 'fadeInUp',
    FLASH = 'flash',
    HEART_BEAT = 'heartBeat',
    SLIDE_IN_LEFT = 'slideInLeft',
    SLIDE_IN_RIGHT = 'slideInRight',
    SLIDE_IN_UP = 'slideInUp',
    SWING = 'swing',
    ZOOM_IN = 'zoomIn'
}

export enum OutAnimationType {
    FADE_OUT = 'fadeOut',
    SLIDE_OUT_LEFT = 'slideOutLeft',
    SLIDE_OUT_RIGHT = 'slideOutRight',
    SLIDE_OUT_UP = 'slideOutUp',
    ZOOM_OUT = 'zoomOut'
}

export enum DialogType {
    WARNING = 'warning',
    INFO = 'info',
    DANGER = 'danger',
    SUCCESS = 'success'
}

export interface OptionDialogButton {
    name: string;
    onClick?: () => void;
    style?: React.CSSProperties;
    type?: 'confirm' | 'cancel'
}

export declare type Message = string;
export declare type ValidationValue = boolean | number | string | Date | RegExp;
export declare type ValidationOption<Value extends ValidationValue = ValidationValue> = ValidationValueMessage<Value>;
export declare type ValidationValueMessage<Value extends ValidationValue = ValidationValue> = {
    value: Value;
    message?: Message;
};
export declare type ValidateResult = Message | boolean | undefined;
export declare type Validate = (data: any) => ValidateResult | Promise<ValidateResult>;
export declare type ValidationOptions = Partial<{
    required: ValidationOption<boolean>;
    min: ValidationOption<number>;
    max: ValidationOption<number>;
    minDate: ValidationOption<Date>;
    maxDate: ValidationOption<Date>;
    maxLength: ValidationOption<number>;
    minLength: ValidationOption<number>;
    pattern: ValidationOption<RegExp>;
    validate: Validate | Record<string, Validate>;
}>;

interface OptionDialogOptions {
    animationType?: AnimationType;
    outAnimationType?: OutAnimationType;
    cancelText?: string;
    confirmText?: string;
    containerStyle?: React.CSSProperties;
    footerStyle?: React.CSSProperties;
    headerStyle?: React.CSSProperties;
    headerTextStyle?: React.CSSProperties;
    onCancel?: () => void;
    onConfirm?: () => void;
    optionButtons?: Array<OptionDialogButton>;
    showCloseButton?: boolean;
    text?: string;
    textStyle?: React.CSSProperties;
    title?: string,
    type?: DialogType;
    bodyComponent?: JSX.Element;
    allowOutsideClick?: boolean;
}

export interface InputProps {
    validation?: ValidationOptions;
    default?: string | Date | number;
    inputType: 'text' | 'file' | 'number' | 'textarea' | 'date' | 'image';
    label?: string;
    /*
        The name that will be used in the response to read the value
     */
    name: string;
    placeholder?: string;
}

export interface ImageInputProps extends InputProps{
    inputType: 'image';
    multiple?: boolean;
}

interface InputDialogOptions {
    animationType?: AnimationType;
    outAnimationType?: OutAnimationType;
    cancelText?: string;
    confirmText?: string;
    containerStyle?: React.CSSProperties;
    footerStyle?: React.CSSProperties;
    headerStyle?: React.CSSProperties;
    headerTextStyle?: React.CSSProperties;
    errorMessageStyle?: React.CSSProperties;
    input?: InputProps | ImageInputProps;
    inputs?: Array<InputProps | ImageInputProps>;
    onCancel?: () => void;
    onConfirm?: (result: { [key: string]: any }) => void;
    onDismissed?: () => void;
    options?: Array<OptionDialogButton>;
    showCloseButton?: boolean;
    text?: string;
    textStyle?: React.CSSProperties;
    title?: string;
    type?: DialogType;
    allowOutsideClick?: boolean;
}

interface AlertOptions {
    animationType?: AnimationType;
    outAnimationType?: OutAnimationType;
    confirmText?: string;
    containerStyle?: React.CSSProperties;
    footerStyle?: React.CSSProperties;
    headerStyle?: React.CSSProperties;
    headerTextStyle?: React.CSSProperties;
    showCloseButton?: boolean;
    text?: string;
    textStyle?: React.CSSProperties;
    title?: string;
    type?: DialogType;
    bodyComponent?: JSX.Element;
    allowOutsideClick?: boolean;
}

export interface ToastOptions {
    containerStyle?: React.CSSProperties;
    customComponent?: JSX.Element;
    position?: ToastPosition;
    text?: string;
    textStyle?: React.CSSProperties,
    timeoutDuration?: number;
    type: DialogType;
    showCloseButton?: boolean;
}

export type IToast = ToastOptions & { id: string };

export interface ModalOptions {
    animationType?: AnimationType,
    outAnimationType?: OutAnimationType,
    timeoutDuration?: number;
}

interface PopupContext {
    component?: () => JSX.Element;
    componentJSX?: JSX.Element;
    componentProps?: React.ComponentProps<any>;
    toasts?: Array<IToast>;
    showModal: (component: JSX.Element, options?: ModalOptions) => void;
    hideModal: () => void;
    showAlert: (options: AlertOptions) => void;
    hideAlert: () => void;
    showOptionDialog: (options: OptionDialogOptions) => void;
    showInputDialog: (options: InputDialogOptions) => void;
    showToast: (options: ToastOptions) => void;
    hideToast: (toastId: string) => void;
}

let DefaultPopupActions: PopupContext = {
    showModal: (_component: JSX.Element, _options?: ModalOptions) => null,
    hideModal: () => null,
    showAlert: (_options: AlertOptions) => null,
    hideAlert: () => null,
    showOptionDialog: (_options: OptionDialogOptions) => null,
    showInputDialog: (_options: InputDialogOptions) => null,
    showToast: (_options: ToastOptions) => null,
    hideToast: (_toastId: string) => null,
}

let ExportedPopupActions: Omit<PopupContext, 'componentProps' | 'component' | 'componentJSX' | 'toasts'> = {
    ...DefaultPopupActions
}

const ModalContext = createContext<PopupContext>(DefaultPopupActions);

const {Provider, Consumer: ModalConsumer} = ModalContext;

const reducer = (state: any, {
    type,
    component,
    componentProps,
    componentJSX,
    toast,
    id
}: { type: 'openModal' | 'hideModal' | 'showToast' | 'hideToast', componentJSX?: JSX.Element, component?: any, componentProps?: any, toast?: IToast, id?: string }) => {
    switch (type) {
        case "openModal":
            return {...state, component, componentProps, componentJSX};
        case "hideModal":
            return {...state, component: null, modalProps: {}, componentJSX: null};
        case "showToast":
            return {...state, toasts: [...state.toasts, toast], componentProps};
        case "hideToast":
            const index = state.toasts.findIndex((t: { id: string | undefined; }) => t.id === id);
            return {...state, toasts: [...state.toasts.slice(0, index), ...state.toasts.slice(index + 1)]};
        default:
            throw new Error("Unspecified reducer action");
    }
};

const PopupProvider = ({children}: { children: any }) => {

    const initialState: PopupContext = {
        componentJSX: undefined,
        component: undefined,
        componentProps: {},
        toasts: [],
        showModal: (componentJSX: JSX.Element, options?: ModalOptions) => {
            dispatch({type: "openModal", componentJSX, componentProps: {...options}});
        },
        hideModal: () => {
            dispatch({type: "hideModal"});
        },
        showAlert: (options: AlertOptions) => {
            dispatch({
                    type: 'openModal',
                    component: Dialog, componentProps: {isAlert: true, isInputDialog: false, ...options}
                }
            )
        },
        hideAlert: () => {
            dispatch({type: "hideModal"})
        },
        showOptionDialog: (options: OptionDialogOptions) => {
            dispatch({
                type: "openModal",
                component: Dialog,
                componentProps: {
                    isAlert: false,
                    isInput: false,
                    ...options
                }
            });
        },
        showInputDialog: (options: InputDialogOptions) => {
            dispatch({
                type: "openModal",
                component: Dialog,
                componentProps: {
                    isAlert: false,
                    isInput: true,
                    ...options
                }
            });
        },
        showToast: (options: ToastOptions) => {
            dispatch({
                type: 'showToast',
                toast: {...options, id: Math.random().toString(36).substring(7)},
                componentProps: {...options}
            })
        },
        hideToast: (toastId?: string) => {
            dispatch({
                type: 'hideToast',
                id: toastId
            })
        }
    };

    ExportedPopupActions = initialState;

    const [state, dispatch] = useReducer(reducer, initialState);
    return (
        <div>
            <Provider value={state}>
                <ModalRoot/>
                {children}
            </Provider>
        </div>
    );
};

const usePopup = () => useContext(ModalContext);


export {ModalConsumer, PopupProvider, usePopup, ExportedPopupActions as PopupActions};
