import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'
import queryString from 'query-string';
import { useLocation } from 'react-router'




import '@djthoms/pretty-checkbox';



import {Helmet} from "react-helmet";

const url = window.location.href
  if(url == "http://localhost:3000/dashboard/settings"){
    require("bootstrap/dist/css/bootstrap.min.css")
    require("./style.css")
  }





function Login() {
    let history = useHistory();

    Axios.defaults.withCredentials = true;

    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    //Login checker
    const [loggedin , setLoggedIn] = useState(false);

    const [documentName , setDocumentName] = useState("");

    const [tags, setTags] = React.useState([])

    const [checkbox , setCheckbox] = useState(false);

    Axios.defaults.withCredentials = true;
    let process = (new URLSearchParams(window.location.search)).get("process")
    const location = useLocation();

    const site = queryString.parse(location.search);








    const checkboxState = (e) =>{
      e.preventDefault()
      console.log(checkbox)
      setCheckbox(!checkbox)

    }




    const newEntry = (e) =>{
      e.preventDefault();
      setSuccesMsg("")
      setErrorMsg("")
      Axios.post("http://localhost:3001/api/newDocument", {
          docName: documentName,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
              setTimeout(function(){
                history.push(
                  "/documents/" + response.data.url
                )
              }, 2000)

          }
      });
    };


    const deleteDoc = (e) =>{
      setErrorMsg("")
      setSuccesMsg("")
      e.preventDefault()
      if(checkbox === false){
        setErrorMsg("Please check the checkbox")
      }else{
        Axios.post("http://localhost:3001/api/deletedoc", {
          docurl: site.id,
        }).then((response)  => {
            if(response.data.error){
                setErrorMsg(response.data.error);
            }else{
                setSuccesMsg(response.data.succes);
                setTimeout(function(){
                  history.push(
                    "/documents/" + response.data.url
                  )
                }, 2000)

            }
        });
      }

    };



    const save = (e) =>{
      e.preventDefault();
      setSuccesMsg("")
      setErrorMsg("")
      console.log(documentName) 
      setErrorMsg("")
      Axios.post("http://localhost:3001/api/updatedoc", {
          docName: documentName,
          docid: site.id,
          users: tags.join("+")
      }).then((response)  => {
          if(response.data.fatalerror){
              history.push("/dashboard")
          }else{
            if(response.data.error){
              setErrorMsg(response.data.error)
            }else{
              setSuccesMsg(response.data.succes);
              setTimeout(function(){
                history.push(
                  "/dashboard/" + response.data.url
                )
              }, 2000)
            }


          }
      });
    };










    return (      
      <div className="container">
      <div className="row gutters">
        <div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
          <div className="card h-100">
            <div className="card-body">
              <div className="account-settings">
                <div className="user-profile">
                  <div className="user-avatar">
                    <img src="https://bootdey.com/img/Content/avatar/avatar1.png" alt="Maxwell Admin" />
                  </div>
                  <h5 className="user-name">Yuki Hayashi</h5>
                  <h6 className="user-email">yuki@Maxwell.com</h6>
                </div>
                <div className="about">
                  <h5 className="mb-2 text-primary">About</h5>
                  <p>I'm Yuki. Full Stack Designer I enjoy creating user-centric, delightful and human experiences.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
          <div className="card h-100">
            <div className="card-body">
              <div className="row gutters">
                <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                  <h6 className="mb-3 text-primary">Personal Details</h6>
                </div>
                <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                  <div className="form-group">
                    <label htmlFor="fullName">Full Name</label>
                    <input type="text" className="form-control" id="fullName" placeholder="Enter full name" />
                  </div>
                </div>
                <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                  <div className="form-group">
                    <label htmlFor="eMail">Email</label>
                    <input type="email" className="form-control" id="eMail" placeholder="Enter email ID" />
                  </div>
                </div>
                <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                  <div className="form-group">
                    <label htmlFor="phone">Phone</label>
                    <input type="text" className="form-control" id="phone" placeholder="Enter phone number" />
                  </div>
                </div>
                <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                  <div className="form-group">
                    <label htmlFor="website">Website URL</label>
                    <input type="url" className="form-control" id="website" placeholder="Website url" />
                  </div>
                </div>
              </div>
              <div className="row gutters">
                <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                  <h6 className="mb-3 text-primary">Address</h6>
                </div>
                <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                  <div className="form-group">
