import React from 'react';
import HeroSection from '../../HeroSection';
import { loginBg} from './Data';
import Pricing from '../../Pricing';
import {Forms} from "react-bootstrap"
import "./design.css"
import 'bootstrap/dist/css/bootstrap.min.css';

function Login() {

    //Email Domains
    const customDomains = ['web.de', 'yahoo.com', 'gmail.com', 'arcor.de' , 't-online.de', 'gmx.de' , 'gmx.net'];
    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [loginStatus, setLoginStatus] = useState("");

    Axios.defaults.withCredentials = true;

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/login").then((response) => {
            if(response.data.loggedin == true){
                setLoginStatus(response.data.user);
            }
            
        })

    }, [])

    const Login = (e) =>{
        e.preventDefault()
        setErrorMsg("");
        setSuccesMsg("");
        Axios.post("http://localhost:3001/api/login", {
            username: username,
            password: password,
        }).then((response)  => {
            if(response.data.error){
                setErrorMsg(response.data.error);
            }else{
                setSuccesMsg(response.data.succes);
            }
        });
    };

  return (
    <div className="App">
            <div class="col">
            <h1>Login to DocSafer</h1>
            {(() => {
                if (errormsg != "") {
                    return (
                        <div class="alert alert-danger" role="alert">
                            {errormsg}
                        </div>
                    )
                } 
                })()} 
                {(() => {
                    if (succesMsg != "") {
                        return (
                            <div class="alert alert-success" role="alert">
                                {succesMsg}
                            </div>
                        )
                    } 
                    })()}
            <br></br>
            <br></br>
            <form onSubmit={Login}>

                <div class="form-group">
                    <input 
                    type="text" 
                    class="form-control username " 
                    id="username" 
                    placeholder="Username or Email" 
                    onChange={(e) => {
                        setUsername(e.target.value)
                    }}/>              
                </div>

                <div class="form-group">
                    <input 
                    type="password" 
                    class="form-control passwordinput" 
                    id="exampleInputPassword1" 
                    placeholder="Password" 
                    onChange={(e) => {
                        setPassword(e.target.value)
                    }}/>
                </div>

 
                <button type="submit" type="hiden" class="btn btn-primary submitbuttonregister">Submit</button>

             </form>
            <button type="submit" class="btn btn-primary registerbutton" onClick={Login}>Submit</button>
        
            </div>
          
        </div>
  );
}

export default Login;
