import "./design.css"
import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";




  

function Login() {

    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [firstname, setFirstName] = useState("");
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [repeatpassword, setRepeatPassword] = useState("");
    const [takenusernames, setTakenUserNames] = useState([]);

    Axios.defaults.withCredentials = true;

    const register = (e) =>{
      e.preventDefault();
      setErrorMsg("");
      setSuccesMsg("");
      Axios.post("http://localhost:3001/api/register", {
          name: name,
          username: username,
          email: email,
          password: password,
          repeatpassword: repeatpassword,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
          }
      });
    };

    return (
      <>
          <div class="form-body">
            <div class="row">
                <div class="form-holder">
                    <div class="form-content">
                        <div class="form-items">
                          <br/>
                            <h3>Register Today</h3>
                            {(() => {
                              if (errormsg != "") {
                                  return (
                                      <div class="invalid-feedback mv-up">
                                        {errormsg}
                                      </div>
                                  )
                              } 
                            })()}
                            {(() => {
                              if (succesMsg != "") {
                                  return (
                                      <div class="valid-feedback mv-up">
                                        {succesMsg}
                                      </div>
                                  )
                              } 
                            })()}  
                            <p>Fill in the data below.</p>
                            <form class="requires-validation" novalidate>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="name" 
                                  placeholder="Full Name"
                                  onChange={(e) => {
                                    setName(e.target.value)
                                  }}   
                                  required/>
                                </div>
                                <div class="col-md-12">
                                    <input 
                                    class="form-control" 
                                    type="email" 
                                    name="email" 
                                    placeholder="E-mail Address"
                                    onChange={(e) => {
                                      setEmail(e.target.value)
                                    }}
                                    required/>
                                </div>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="username" 
                                  placeholder="username"
                                  onChange={(e) => {
                                    setUsername(e.target.value)
                                  }}   
                                  required/>
                                </div>
                              <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="password" 
                                  name="password" 
                                  placeholder="Password"
                                  onChange={(e) => {
                                    setPassword(e.target.value)
                                 }}
                                  required/>
                              </div>
                              <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="password" 
                                  name="repeatpassword" 
                                  placeholder="Repeat Password"
                                  onChange={(e) => {
                                    setRepeatPassword(e.target.value)
                                 }}
                                  required/>
                              </div>
                              <br/>
                            <div class="fom-check">
                              <input class="form-check-input" type="checkbox" value="" id="invalidCheck" required/>
                              <label class="form-check-label"> I confirm that all data are correct</label>
                            </div>

                            <br/>
                                <div class="form-button mt-3">
                                    <button id="submit" type="submit" class="btn btn-primary" onClick={register} >Register</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>

      </>
    );
  }
  
  export default Login;
