import "./design.css"
import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'


function Register() {
    let history = useHistory();

    Axios.defaults.withCredentials = true;

    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [firstname, setFirstName] = useState("");
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [repeatpassword, setRepeatPassword] = useState("");
    const [takenusernames, setTakenUserNames] = useState([]);
    //Login checker
    const [loggedin , setLoggedIn] = useState(false);

    
    Axios.defaults.withCredentials = true;
    Axios.get("http://localhost:3001/api/check", {
        }).then((response)  => {
            if(!response.data.error){
                console.log(response.data.error)
                history.push('/dashboard')
            }
    });

    const register = (e) =>{
      e.preventDefault();
      setErrorMsg("");
      setSuccesMsg("");
      Axios.post("http://localhost:3001/api/register", {
          name: name,
          username: username,
          email: email,
          password: password,
          repeatpassword: repeatpassword,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
          }
      });
    };

    return (
      <>
          <div>
        <meta charSet="utf-8" />
        <meta name="author" content="Kodinger" />
        <meta name="viewport" content="width=device-width,initial-scale=1" />
        <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossOrigin="anonymous" />
        <link rel="stylesheet" type="text/css" href="css/my-login.css" />
        <section className="h-100">
          <div className="container h-100">
            <div className="row justify-content-md-center h-100">
              <div className="card-wrapper">
                <div className="card fat">
                  <div className="card-body">
                    <h4 className="card-title">Register</h4>
                      <div className="form-group">
                      {(() => {
                              if (succesMsg != "") {
                                  return (
                                      <div class="valid-feedback mv-up">
                                        {succesMsg}
                                      </div>
                                  )
                              } 
                        })()}  
                        <label htmlFor="name">Full Name</label>
                        <input id="name" type="text" className="form-control" name="name" required autofocus  onChange={(e) => {setName(e.target.value)}}/>
                        <div className="invalid-feedback">
                          {errormsg}
                        </div>
                      </div>
                      <div className="form-group">
                        <label htmlFor="password">Username</label>
                        <input id="password" type="password" className="form-control" name="password" required data-eye onChange={(e) => {setUsername(e.target.value)}} />
                      </div>
                      <div className="form-group">
                        <label htmlFor="password">Email</label>
                        <input id="password" type="password" className="form-control" name="password" required data-eye onChange={(e) => {setEmail(e.target.value)}} />
                      </div>
                      <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input id="password" type="password" className="form-control" name="password" required data-eye onChange={(e) => {setPassword(e.target.value)}} />
                      </div>
                      <div className="form-group">
                        <label htmlFor="password">Repeat Password</label>
                        <input id="password" type="password" className="form-control" name="password" required data-eye onChange={(e) => {setRepeatPassword(e.target.value)}} />
                      </div>
                      <div className="form-group">
                        <div className="custom-checkbox custom-control">
                          <input type="checkbox" name="agree" id="agree" className="custom-control-input" required  />
                          <label htmlFor="agree" className="custom-control-label">I agree to the <a href="#">Terms and Conditions</a></label>
                        </div>
                      </div>
                      <div className="form-group m-0">
                        <button type="submit" className="btn btn-primary btn-block" color="#16DF7E" onClick={register}>
                          Register
                        </button>
                      </div>
                      <div className="mt-4 text-center">
                        Already have an account? <a href="index.html">Login</a>
                      </div>
                  </div>
                </div>
                <div className="footer">
                  Copyright © 2021 — Privel
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>

      </>
    );
  }
  
  export default Register;
