import "./style.css"

import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'
import queryString from 'query-string';
import { useLocation } from 'react-router'




import '@djthoms/pretty-checkbox';



import {Helmet} from "react-helmet";

const url = window.location.href
  if(url == "http://localhost:3000/dashboard/settings"){
    require("bootstrap/dist/css/bootstrap.min.css")
  }





function Login() {
    let history = useHistory();

    Axios.defaults.withCredentials = true;

    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    //Login checker
    const [loggedin , setLoggedIn] = useState(false);

    const [documentName , setDocumentName] = useState("");

    const [tags, setTags] = React.useState([])

    const [checkbox , setCheckbox] = useState(false);

    Axios.defaults.withCredentials = true;
    let process = (new URLSearchParams(window.location.search)).get("process")
    const location = useLocation();

    const site = queryString.parse(location.search);








    const checkboxState = (e) =>{
      e.preventDefault()
      console.log(checkbox)
      setCheckbox(!checkbox)

    }




    const newEntry = (e) =>{
      e.preventDefault();
      setSuccesMsg("")
      setErrorMsg("")
      Axios.post("http://localhost:3001/api/newDocument", {
          docName: documentName,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
              setTimeout(function(){
                history.push(
                  "/documents/" + response.data.url
                )
              }, 2000)

          }
      });
    };


    const deleteDoc = (e) =>{
      setErrorMsg("")
      setSuccesMsg("")
      e.preventDefault()
      if(checkbox === false){
        setErrorMsg("Please check the checkbox")
      }else{
        Axios.post("http://localhost:3001/api/deletedoc", {
          docurl: site.id,
        }).then((response)  => {
            if(response.data.error){
                setErrorMsg(response.data.error);
            }else{
                setSuccesMsg(response.data.succes);
                setTimeout(function(){
                  history.push(
                    "/documents/" + response.data.url
                  )
                }, 2000)

            }
        });
      }

    };



    const save = (e) =>{
      e.preventDefault();
      setSuccesMsg("")
      setErrorMsg("")
      console.log(documentName) 
      setErrorMsg("")
      Axios.post("http://localhost:3001/api/updatedoc", {
          docName: documentName,
          docid: site.id,
          users: tags.join("+")
      }).then((response)  => {
          if(response.data.fatalerror){
              history.push("/dashboard")
          }else{
            if(response.data.error){
              setErrorMsg(response.data.error)
            }else{
              setSuccesMsg(response.data.succes);
              setTimeout(function(){
                history.push(
                  "/dashboard/" + response.data.url
                )
              }, 2000)
            }


          }
      });
    };










    return (      
      <div className="container mt-5">
      <div className="row">
        <div className="col-lg-4 pb-5">
