import { useCallback, useEffect, useState, Component } from "react"
import Quill from "quill"
import "quill/dist/quill.snow.css"
import { io } from "socket.io-client"
import { useParams, Link, useLocation, useHistory, BrowserRouter as Router} from "react-router-dom"
import queryString from 'query-string'
import Axios from "axios"


const SAVE_INTERVAL_MS = 2000
const TOOLBAR_OPTIONS = [
  [{ header: [1, 2, 3, 4, 5, 6, false] }],
  [{ font: [] }],
  [{ list: "ordered" }, { list: "bullet" }],
  ["bold", "italic", "underline"],
  [{ color: [] }, { background: [] }],
  [{ script: "sub" }, { script: "super" }],
  [{ align: [] }],
  ["image", "blockquote", "code-block"],
  ["clean"],
]

export default function TextEditor() {
  let history = useHistory();
  const { id: documentId } = useParams()
  const [socket, setSocket] = useState()
  const [quill, setQuill] = useState()
  const [documentID, setDocumentID] = useState("")
  const [noError, setNoError] = useState(false)

  useEffect(() => {
    const s = io("http://localhost:3002")
    setSocket(s)

    return () => {
      s.disconnect()
    }
  }, [])



  useEffect(() => {
    if (socket == null || quill == null) return

    socket.once("load-document", document => {
      quill.setContents(document)
      quill.enable()
    })

    socket.emit("get-document", documentId)
    setDocumentID(documentId)
  }, [socket, quill, documentId])

  Axios.defaults.withCredentials = true;
  Axios.get("http://localhost:3001/api/check", {
        }).then((response)  => {
          if(response.data.error){
            history.push("/dashboard")
          }else{
            setNoError(true)
          }
  });

  useEffect(() => {
    Axios.post("http://localhost:3001/api/texteditor", {
      id: documentID
      }).then((response)  => {
  });
  }, )
  useEffect(() => {
    if(noError){
      useEffect(() => {
        if (socket == null || quill == null) return

        const interval = setInterval(() => {
          socket.emit("save-document", quill.getContents())
        }, SAVE_INTERVAL_MS)

        return () => {
          clearInterval(interval)
        }
      }, [socket, quill])

      useEffect(() => {
        if (socket == null || quill == null) return

        const handler = delta => {
          quill.updateContents(delta)
        }
        socket.on("receive-changes", handler)

        return () => {
          socket.off("receive-changes", handler)
        }
      }, [socket, quill])

      useEffect(() => {
        if (socket == null || quill == null) return

        const handler = (delta, oldDelta, source) => {
          if (source !== "user") return
          socket.emit("send-changes", delta)
        }
        quill.on("text-change", handler)

        return () => {
          quill.off("text-change", handler)
        }
      }, [socket, quill])

      const wrapperRef = useCallback(wrapper => {
        if (wrapper == null) return

        wrapper.innerHTML = ""
        const editor = document.createElement("div")
        wrapper.append(editor)
        const q = new Quill(editor, {
          theme: "snow",
          modules: { toolbar: TOOLBAR_OPTIONS },
        })
        q.disable()
        q.setText("Loading...")
        setQuill(q)
      }, [])
      return <div className="container" ref={wrapperRef}></div>

    }
  }, )



}
