import "./design.css"
import Axios from "axios";
import React, {useState, useEffect} from 'react';
import {Buttons, Forms, Grid, FormControl, Accordion} from "react-bootstrap";
import  { Redirect, useHistory  } from 'react-router-dom'



function Changepw() {
    let history = useHistory();

    const [password, setPassword] = useState("")
    const [repeatpassword, setRepeatPassword] = useState("")
    const [errormsg, setErrorMsg] = useState("");
    const [succesMsg, setSuccesMsg] = useState("");

    Axios.post("http://localhost:3001/api/changepwauth", {
        process: this.match.params.process,
    }).then((response)  => {
        if(response.data.error){
            history.push('/')
        }
    });



    const change = (e) =>{
      e.preventDefault();
      setErrorMsg("");
      setSuccesMsg("");

        Axios.post("http://localhost:3001/api/changepw", {
          password: password,
          repeatpassword: repeatpassword,
      }).then((response)  => {
          if(response.data.error){
              setErrorMsg(response.data.error);
          }else{
              setSuccesMsg(response.data.succes);
          }
      });
    };
    return (
      <>
          <div class="form-body">
            <div class="row">
                <div class="form-holder">
                    <div class="form-content">
                        <div class="form-items">
                          <br/>
                            <h3>Change Password</h3>
                            {(() => {
                              if (errormsg != "") {
                                  return (
                                      <div class="invalid-feedback mv-up">
                                        {errormsg}
                                      </div>
                                  )
                              } 
                            })()}
                            {(() => {
                              if (succesMsg != "") {
                                  return (
                                      <div class="valid-feedback mv-up">
                                        {succesMsg}
                                      </div>
                                  )
                              } 
                            })()}  
                            <form class="requires-validation" novalidate>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="username" 
                                  placeholder="New Password"
                                  onChange={(e) => {
                                    setPassword(e.target.value)
                                  }}   
                                  required/>
                                </div>
                                <div class="col-md-12">
                                  <input 
                                  class="form-control" 
                                  type="text" 
                                  name="username" 
                                  placeholder="Repeat New Password"
                                  onChange={(e) => {
                                    setRepeatPassword(e.target.value)
                                  }}   
                                  required/>
                                </div>
                            <br/>
                            <br/>
                            <br/>
                                <div class="form-button mt-3">
                                    <button id="submit" type="submit" class="btn btn-primary" onClick={change} >Reset Password</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>

      </>
    );
  }
  
  export default Changepw;
